        public static string GetProductionOrderOutContentStr(Barcode_Model barcode, int dpi = 300)
        {
            StringBuilder sbPrint = new StringBuilder();

            #region Ready
            StringBuilder sbReturn = new StringBuilder(10240);
            FontConvertBmp fcb = new FontConvertBmp();

            Point Start;
            int Width = 0;
            int Heigh = 0;

            string BasicFontName = "黑体";
            int BasicFontSize = 1;

            int BaiscColMargin = 0;
            int BasicRowHeigh = 0;
            int MinRowMargin = 0;

            int PictrueTopPad = 0;
            int PictrueRightPad = 0;
            int ROHSMarginQR = 0;
            int QRCodeSize = 0;

            int Line = 0;

            #endregion


            switch (dpi)
            {
                case 200:
                    {
                        #region Parameter

                        Start = new Point(0, 50);
                        Width = 630;
                        Heigh = 472;

                        BasicFontName = "黑体";
                        BasicFontSize = 8;

                        BaiscColMargin = 150;
                        BasicRowHeigh = 50;
                        MinRowMargin = 10;

                        PictrueTopPad = 40;
