                        ROHSMarginQR = 220;
                        QRCodeSize = 4;

                        #endregion

                    }
                    break;

                case 300:
                    {
                        #region Parameter

                        Start = new Point(20, 50);
                        Width = 945;
                        Heigh = 705;

                        BasicFontName = "黑体";
                        BasicFontSize = 12;

                        BaiscColMargin = 230;
                        BasicRowHeigh = 90;
                        MinRowMargin = 15;

                        PictrueTopPad = 60;
                        PictrueRightPad = 220;
                        ROHSMarginQR = 330;
                        QRCodeSize = 7;

                        #endregion

                    }
                    break;

                default:
                    return string.Empty;
            }

            #region Content
            fcb.GETFONTHEX(barcode.AndalaNo, false, BasicFontName, "txtAndalaNo", (BasicFontSize - 2) * 3, 0, false, false, sbReturn);
            sbPrint.Append(sbReturn.ToString());
            fcb.GETFONTHEX(barcode.CUSNAME, false, BasicFontName, "txtCusName", (BasicFontSize) * 3, 0, false, false, sbReturn);
            sbPrint.Append(sbReturn.ToString());
